import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

class Show extends Component {
    constructor(props) {
        super(props);
        this.state = {
            employee: {},
        };
    }

    componentDidMount() {
        axios.get("/users/" + this.props.match.params.id).then((res) => {
            this.setState({ employee: res.data });
            console.log(this.state.employee);
        });
    }
    render() {
        return ( <
            div className = "container" >
            <
            div className = "panel panel-default" >
            <
            div className = "panel-heading" >
            <
            h3 className = "panel-title" > Employee Details < /h3>{" "} <
            /div>{" "} <
            div className = "panel-body" >
            <
            h4 > { " " } <
            Link to = "/" > { " " } <
            span className = "glyphicon glyphicon-th-list" > < /span> Employees
            List { " " } <
            /Link>{" "} <
            /h4>{" "} <
            dl >
            <
            dt > ID: < /dt> <dd> {this.state.employee.username} </dd > { " " } <
            dt > FIRST NAME: < /dt> <dd> {this.state.employee.first_name} </dd > { " " } <
            dt > LASST NAME: < /dt> <dd> {this.state.employee.last_name} </dd > { " " } <
            dt > Email: < /dt> <dd> {this.state.employee.email} </dd > { " " } <
            dt > Line Manager Id: < /dt> <
            dd > { this.state.employee.lineManager } < /dd> <
            /dl>{" "} <
            Link to = { `/apply/${this.state.employee.username}` }
            className = "btn btn-primary" >
            { " " }
            Apply Leave { " " } <
            /Link>{" "} <
            /div>{" "} <
            /div>{" "} <
            /div>
        );
    }
}
export default Show;
